import React, {useState} from 'react'     //using of react Hooks Search react Hooks on google.  

export default function TextForm(props) {

    const handleUpClick = ()=>{
    //consol.log("Uppercase was clicked + text")
    let Text = text.toUpperCase();  //using of toUppercase() Function.
    setText(Text) //Text = Variable.
    }

    const handleOnChange = (event) =>{
        //consol.log("Onchange");
        setText(event.target.value)
    }

    const [text, setText] = useState('Enter text here');  // https://legacy.reactjs.org/docs/hooks-intro.html
    //text="new text"; // Wrong way to change the state.
    //setText("new text"); //Correct way to change the state.
  return (
    <div>
        <h2>{props.heading}</h2>
        <div className="mb-3">
        <textarea className="form-control" value={text} onChange={handleOnChange} id="myBox" rows="8"></textarea>   
            {/* value={text}, onChange={handleOnChange} add textarea tag */}
        </div>
        <button className="btn btn-primary" onClick={handleUpClick}>Convert to Uppercase</button>  
            {/* onClick={handleUpclick add} */}
    </div>
  )
}
